class Queue{
    private LinkedList link;

    public Queue() {
        link = new LinkedList();
    }

    public void myAdd(E obj) {
        link.addFirst(obj);
    }

    public E myGet(){
        return link.removeLast();
    }

    public boolean isNull(){
        return link.isEmpty();
    }

