    /**
     * 删除一个目录
     */
    private static void removeDir(File dir) {
        //1. 列出当前目录下的文件以及文件夹file[]
        File[] files = dir.listFiles();

        for(File file: files){
            //2. 对遍历的对象进行判断是否是目录
            if(file.isDirectory()){
                removeDir(file);
            }else{
                System.out.println(file + ":"+file.delete());
            }
        }
        System.out.println(dir+":"+dir.delete());
    }
}



